<template>
  <section
    v-show="isActive"
    :aria-hidden="!isActive"
    class="obg-n-tabs-component-panel"
    :id="hash"
    role="tab-panel"
  >
    <slot/>
  </section>
</template>

<script>
  /**
   * @see Nissan only
   * @class n-tab
   * @classdesc components/n-tab
   * @param {string} [id]
   * @param {string} [name] Is required
   * @param {string} [icon=''] icon
   * @param {string} [prefix='']
   * @param {string} [suffix='']
   * @param {boolean} [isDisable=false]
   * @param {slot} [slot] section of tab for content
   *
   * @example
   * <obg-n-tabs
   * :selectedColor="'orange'"
   * >
   *   <obg-n-tab
   *    :name="'label'"
   *    >
   *    label contents
   *    </obg-n-tab>
   *   <obg-n-tab
   *    :name="'content'"
   *    >
   *    content.contents
   *    </obg-n-tab>
   *   <obg-n-tab
   *    :name="'play'"
   *    :isDisable="true"
   *    >
   *    play.contents
   *    </obg-n-tab>
   * </obg-n-tabs>
   */
  export default {
    name: 'obg-n-tab',
    props: {
      id: {default: null},
      name: {required: true},
      prefix: {default: ''},
      suffix: {default: ''},
      icon: {default: ''},
      isDisable: {default: false}
    },
    data () {
      return {
        isActive: false,
        isVisible: true
      }
    },
    computed: {
      header () {
        return this.prefix + this.name + this.suffix
      },

      hash () {
        if (this.isDisable) {
          return '#'
        }

        return this.id ? '#' + this.id : '#' + this.name.toLowerCase().replace(/ /g, '-')
      }
    }
  }
</script>

