/**
 * @module util
 * @desc utility functions.
 * @example 
 * var appFrm = window.applicationFramework
 * var util = appFrm.util
 */

class util{

  /**
   * @function getOSVersion 
   * @summary return OS version
   * @return {string} ver - OS version
   * @example
   * var ver = applicationFramework.util.getOSVersion()
   *
   */
  getOSVersion () {
  }

  /**
   * @function getLanguage
   * @summary to get current language of widgets
   * @return {string} lang - widgets language
   * @example
   * var lang = applicationFramework.util.getLanguage()
   *
   */
  getLanguage () {
  }

  /**
   * @function getAmbientColor
   * @summary to get ambient colors
   * @return {string} ambientColor - ambient color
   * @example
   * var ambientColor = applicationFramework.util.getAmbientColor()
   *
   */
  getAmbientColor () {
  }

  /**
   * @function getDeviceModel 
   * @summary to get device model
   * @return {string} deviceModel
   * @example
   * var deviceModel = applicationFramework.util.getDeviceModel()
   *
   */
  getDeviceModel () {
  }

  /**
   * @function getSteeringWheelPosition 
   * @summary return device's steeringwheel(handle) position
   * @return {string} steeringWheelPosition - right=1,left=2 
   * @example
   * var steeringWheelPosition = applicationFramework.util.getSteeringWheelPosition()
   *
   */
  getSteeringWheelPosition () {
  }

  /**
   * @function getLoggingLevel 
   * @summary return logging level
   * @return {integer} loggingLevel - logging level 
   * @example
   * var loggingLevel = applicationFramework.util.getLoggingLevel()
   *
   */
  getLoggingLevel () {
  }

  /**
   * @function getBaseUrl 
   * @summary return base url provided by alliance
   * @return {string} baseUrl 
   * @example
   * var baseUrl = applicationFramework.util.getBaseUrl()
   *
   */
  getBaseUrl () {
  }
}

